    private void bt_updatetablaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_bt_updatetablaMouseClicked
        // TODO add your handling code here:
        //select ID,[Order ID],[Order Date],[Customer ID],Country,City,[Product ID],Sales
        //from TenRecord
        DefaultTableModel modelo = (DefaultTableModel) tabla.getModel();
        Dba db = new Dba("./DataBase6.mdb");
        db.conectar();
        while (modelo.getRowCount() > 0) {
            modelo.removeRow(0);
        }
        try {
            db.query.execute("select [Row ID],[Order ID],[Order Date],[Customer ID],Country,City,[Product ID],Sales from TenRecord");//ejecuta query
            ResultSet rs = db.query.getResultSet();//tabla pero en memoria de java
            while (rs.next()) {
                //rs.getInt(1) + "--->" + rs.getString(2)
                String ID = rs.getString(1);
                String OrderId = rs.getString(2);
                String OrderDate = rs.getString(3);
                String CustomerId = rs.getString(4);
                String Country = rs.getString(5);
                String City = rs.getString(6);
                String PorductId = rs.getString(7);
                String Sales = rs.getString(8);

                Object[] row = {ID, OrderId, OrderDate, CustomerId, Country, City, PorductId, Sales};
                modelo.addRow(row);
                tabla.setModel(modelo);
               
            }
             JOptionPane.showMessageDialog(this, "Listado exitosamente!");
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(null, "Algo salio mal! OOPS!");
        }
        db.desconectar();
    }//GEN-LAST:event_bt_updatetablaMouseClicked

